sys_script_include.script:
var getChoiceListRecord = Class.create();
getChoiceListRecord.prototype = {
    initialize: function() {
    },
	getChoice: function(){
		var gr=new GlideRecord("sys_choice");
		gr.query(grResponse);
		function grResponse(gr){
			while(gr.next()){
				gr.addInfoMessage(gr.name);
			}
		}
	},
    type: 'getChoiceListRecord'
};
